var inlineCKEditors=[];$(document).ready(function(){var $inlines=$(".InputfieldCKEditorInline");var pageID=$("#Inputfield_id").val();if($inlines.size()>0){CKEDITOR.disableAutoInline=true;$inlines.mouseover(function(){var $t=$(this);if($t.is(".InputfieldCKEditorLoaded"))return;$t.effect("highlight",{},500);$t.attr("contenteditable","true");var configName=$t.attr("data-configName");var editor=CKEDITOR.inline($(this).attr("id"),config[configName]);var n=inlineCKEditors.length;inlineCKEditors[n]=editor;$t.attr("data-n",n);$t.addClass("InputfieldCKEditorLoaded")});$("form.InputfieldForm").submit(function(){$(this).find(".InputfieldCKEditorInline").each(function(){var $t=$(this);var value;if($t.is(".InputfieldCKEditorLoaded")){var n=parseInt($t.attr("data-n"));var editor=inlineCKEditors[n];value=editor.getData()}else{value=$t.html()}var $input=$t.next("input");$input.attr("value",value)})})}});